<template>
  <transition
    :name="transition"
    mode="in-out"
    appear
    :appear-active-class="enterClass"
    :enter-active-class="enterClass"
    :leave-active-class="leaveClass"
    @beforeEnter="beforeEnter"
    @afterEnter="afterEnter"
    @beforeLeave="beforeLeave"
    @afterLeave="afterLeave"
  >
    <div :class="classes" v-if="show">
      <div class="modal-background" @click="deactive"></div>
      <div class="modal-card">
        <header class="modal-card-head">
          <slot name="modal-card-title">
            <p class="modal-card-title">{{ title }}</p>
          </slot>
          <button class="delete" @click="deactive" v-if="closable"></button>
        </header>
        <section class="modal-card-body">
          <slot></slot>
        </section>
        <slot name="footer">
          <footer class="modal-card-foot">
              <a class="button is-primary" :class="okButtonClass" @click="ok">{{ okText }}</a>
              <a class="button" :class="cancelButtonClass" @click="cancel">{{ cancelText }}</a>
          </footer>
        </slot>
      </div>
    </div>
  </transition>
</template>

<script>
import BaseModal from './BaseModal'

export default {
  mixins: [BaseModal],

  props: {
    title: {
      type: String
    },
    okText: {
      type: String,
      default: 'Ok'
    },
    cancelText: {
      type: String,
      default: 'Cancel'
    },
    okButtonClass: {
      type: Object,
      default() {
        return {
          'is-small': true
        }
      }
    },
    cancelButtonClass: {
      type: Object,
      default() {
        return {
          'is-small': true
        }
      }
    }
  },

  computed: {
    classes() {
      return ['modal', 'animated', 'is-active']
    }
  },

  methods: {
    ok() {
      this.$emit('ok')
    },

    cancel() {
      this.$emit('cancel')
    }
  }
}
</script>
